mapper.select:
SELECT t_projectsource.sourceName, t_projecttype.typeName, t_project.id, t_project.projectName, t_project.marchSpecialty, t_project.filePath, t_project.teacherNames, t_project.selectCount, t_project.select_flag, t_project.verifyProject_flag, t_project.release_flag FROM t_project INNER JOIN t_projectsource ON t_project.id_projectSource = t_projectsource.id INNER JOIN t_projecttype ON t_project.id_projectType = t_projecttype.id INNER JOIN t_teacher ON t_project.id_teacher = t_teacher.id INNER JOIN t_section ON t_teacher.id_section = t_section.id INNER JOIN t_institute ON t_section.id_institute = t_institute.id WHERE t_project.verifyProject_flag=2
and t_institute.id =#{institute_id}
and t_section.id =#{section_id}
and t_project.id =#{name} or t_project.projectName=#{name} or t_project.teacherNames LIKE '%#{name}%'

SELECT t_project.id, t_project.id_projectType, t_project.id_projectSource, t_project.id_teacher, t_project.projectName, t_project.marchSpecialty, t_project.filePath, t_project.teacherNames, t_project.selectCount, t_project.select_flag, t_project.verifyProject_flag, t_project.release_flag, t_projectsource.sourceName, t_projecttype.typeName, t_teacher.userName, t_section.section_name FROM t_project INNER JOIN t_teacher ON t_project.id_teacher = t_teacher.id INNER JOIN t_section ON t_teacher.id_section = t_section.id INNER JOIN t_institute ON t_section.id_institute = t_institute.id INNER JOIN t_projectsource ON t_project.id_projectSource = t_projectsource.id INNER JOIN t_projecttype ON t_project.id_projectType = t_projecttype.id 1=1
and t_institute.id =#{institute_id}
and t_section.id =#{section_id}
and t_section.section_name =#{section_name}
order by CONVERT(section_name using utf8)
